import React from "react";
import fetch from "node-fetch";
import TableComponent from "./TableComponent";

const styles = {
  countDiv: {
    height: "13vh",
    backgroundColor: "#b4bec3",
    width: "7%",
    borderRadius: "10px",
    marginRight: "1%",
    display: "flex",
    flexDirection: "column",
    justifyContent: "space-around",
    alignItems: "center",
  },
  DeliveryCounterDiv: {
    height: "13vh",
    backgroundColor: "blue",
    width: "7%",
    borderRadius: "10px",
    marginRight: "1%",
    display: "flex",
    flexDirection: "column",
    justifyContent: "space-around",
    alignItems: "center",
  },
  countParentDiv: {
    display: "flex",
    justifyContent: "center",
    marginTop: "3%",
  },
  delCounterHeaderSpan: {
    fontSize: "0.79rem",
    color: "white",
    marginRight: "30%",
  },
  delCounterValueSpan: {
    fontSize: "1.4rem",
    color: "white",
  },
  countHeaderSpan: {
    fontSize: "0.79rem",
    color: "blue",
    marginRight: "30%",
  },
  counterValueSpan: {
    fontSize: "1.4rem",
    color: "blue",
  },
  actionButtonDiv: {
    border: "1px solid grey",
    width: "25%",
    height: "45vh",
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    padding: "0px 10px 0px 30px",
  },
  tableActionDiv: {
    display: "flex",
    justifyContent: "space-evenly",
    width: "100%",
    marginTop: "3%",
  },
  tableDiv: {
    height: "65vh",
    width: "70%",
    overflowX: "auto",
    marginBottom: "4%",
  },
  actionButtonStyle: {
    height: "40px",
    border: "1px solid grey",
    display: "flex",
    justifyContent: "space-around",
    alignItems: "center",
  },
};

class HomePageComponent extends React.Component {
  constructor(props) {
    super();
    this.state = {
      allData: [],
      shipmentData: [],
      deliveredData: [],
      outForDelivery: [],
      arrivedMysoreData: [],
      arrivedBangaloreData: [],
      transitNextHub: [],
    };
  }

  componentDidMount() {
    this.fetchJsonData();
  }
  fetchJsonData = (value) => {
    fetch("https://f0ztti2nsk.execute-api.ap-south-1.amazonaws.com/v1/consignment/fetch", {
      method: "post",
      headers: new Headers({
        Authorization: "Bearer tTU3gFVUdP",
        "Content-Type": "application/json",
      }),
      body: JSON.stringify({
        email: "vkaruna7.kv@gmail.com",
      }),
    })
      .then((res) => res.json())
      .then((json) => {
        this.setState({ allData: json }, () => {
          if (value === "outForDelivery") {
            var data = this.state.allData.filter((singleData) => singleData.current_status === "Out for Delivery");
            this.setState({ shipmentData: data, outForDelivery: data });
          } else if (value === "arrivedAtMysore") {
            var data = this.state.allData.filter(
              (singleData) => singleData.current_status === "Undelivered" && singleData.to === "MYSORE"
            );
            this.setState({ shipmentData: data, arrivedMysoreData: "" });
          } else if (value === "arrivedAtBangalore") {
            var data = this.state.allData.filter(
              (singleData) => singleData.current_status === "Undelivered" && singleData.to === "BANGALORE"
            );
            this.setState({ shipmentData: data, arrivedBangaloreData: data });
          } else if (value === "nextHub") {
            var data = this.state.allData.filter((singleData) => singleData.current_status === "Undelivered");
            this.setState({ shipmentData: data, transitNextHub: data });
          } else {
            var data = this.state.allData.filter((singleData) => singleData.current_status_code === "DEL");
            this.setState({ shipmentData: data, deliveredData: data });
          }
        });
      });
  };

  render() {
    console.log(this.state.allData);
    console.log(this.state.shipmentData);
    return (
      <div>
        <div
          style={{
            borderBottom: "groove",
            display: "flex",
            justifyContent: "space-between",
            height: "10vh",
            alignItems: "center",
          }}
        >
          <div>
            <span>Intugine</span>
          </div>
          <div style={{ display: "flex", width: "30%", justifyContent: "space-around" }}>
            <div>
              <span>Home</span>
            </div>
            <div>
              <span>Brands</span>
            </div>
            <div>
              <span>Transporters</span>
            </div>
            <div></div>
          </div>
        </div>
        <div>
          <div style={styles.countParentDiv}>
            <div style={styles.DeliveryCounterDiv}>
              <span style={styles.delCounterHeaderSpan}>DEL</span>
              <span style={styles.delCounterValueSpan}>{this.state.deliveredData.length}</span>
            </div>
            <div style={styles.countDiv}>
              <span style={styles.countHeaderSpan}>INT</span>
              <span style={styles.counterValueSpan}>916</span>
            </div>
            <div style={styles.countDiv}>
              <span style={styles.countHeaderSpan}>OOD</span>
              <span style={styles.counterValueSpan}>{this.state.outForDelivery.length}</span>
            </div>
            <div style={styles.countDiv}>
              <span style={styles.countHeaderSpan}>DEX</span>
              <span style={styles.counterValueSpan}>916</span>
            </div>
            <div style={styles.countDiv}>
              <span style={styles.countHeaderSpan}>NFI</span>
              <span style={styles.counterValueSpan}>916</span>
            </div>
          </div>
          <div style={styles.tableActionDiv}>
            <div style={styles.actionButtonDiv}>
              <div style={styles.actionButtonStyle} onClick={() => this.fetchJsonData("delivered")}>
                <span>Delivered</span>
                <span>04-07-2019</span>
                <span>14:00</span>
              </div>
              <div style={styles.actionButtonStyle} onClick={() => this.fetchJsonData("outForDelivery")}>
                <span>Out Of Delivery</span>
                <span>04-07-2019</span>
                <span>14:00</span>
              </div>
              <div style={styles.actionButtonStyle} onClick={() => this.fetchJsonData("arrivedAtMysore")}>
                <span>Arrived at Mysore</span>
                <span>04-07-2019</span>
                <span>14:00</span>
              </div>
              <div style={styles.actionButtonStyle} onClick={() => this.fetchJsonData("arrivedAtBangalore")}>
                <span>Arrived at Bangalore</span>
                <span>04-07-2019</span>
                <span>14:00</span>
              </div>
              <div style={styles.actionButtonStyle} onClick={() => this.fetchJsonData("nextHub")}>
                <span>Transit to Next Hub</span>
                <span>04-07-2019</span>
                <span>14:00</span>
              </div>
            </div>
            <div style={styles.tableDiv}>
              <TableComponent shipmentData={this.state.shipmentData} />
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default HomePageComponent;
