import React from "react";
import fetch from "node-fetch";
import TableComponent from "./TableComponent";

const styles = {
  countDiv: {
    height: "13vh",
    backgroundColor: "#b4bec3",
    width: "7%",
    borderRadius: "10px",
    marginRight: "1%",
    display: "flex",
    flexDirection: "column",
    justifyContent: "space-around",
    alignItems: "center",
  },
  DeliveryCounterDiv: {
    height: "13vh",
    backgroundColor: "blue",
    width: "7%",
    borderRadius: "10px",
    marginRight: "1%",
    display: "flex",
    flexDirection: "column",
    justifyContent: "space-around",
    alignItems: "center",
  },
  countParentDiv: {
    display: "flex",
    justifyContent: "center",
    marginTop: "3%",
  },
  delCounterHeaderSpan: {
    fontSize: "0.79rem",
    color: "white",
    marginRight: "30%",
  },
  delCounterValueSpan: {
    fontSize: "1.4rem",
    color: "white",
  },
  countHeaderSpan: {
    fontSize: "0.79rem",
    color: "blue",
    marginRight: "30%",
  },
  counterValueSpan: {
    fontSize: "1.4rem",
    color: "blue",
  },
  actionButtonDiv: {
    border: "1px solid grey",
    height: "45vh",
    width: "25%",
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    padding: "0px 10px 0px 30px",
  },
  tableActionDiv: {
    display: "flex",
    justifyContent: "space-evenly",
    width: "100%",
    marginTop: "3%",
  },
  tableDiv: {
    height: "65vh",
    width: "70%",
    overflowX: "auto",
    marginBottom: "4%",
  },
  actionButtonStyle: {
    height: "40px",
    border: "1px solid grey",
    display: "flex",
    justifyContent: "space-around",
    alignItems: "center",
  },
};

class HomePageComponent extends React.Component {
  constructor(props) {
    super();
    this.state = {
      allData: [],
      shipmentData: [],
      deliveredData: [],
      outForDelivery: [],
      arrivedMysoreData: [],
      arrivedBangaloreData: [],
      transitNextHub: [],
      singleShipmentData: "",
    };
  }

  componentDidMount() {
    this.fetchJsonData();
  }
  fetchJsonData = (value) => {
    console.log(value);
    fetch("https://f0ztti2nsk.execute-api.ap-south-1.amazonaws.com/v1/consignment/fetch", {
      method: "post",
      headers: new Headers({
        Authorization: "Bearer tTU3gFVUdP",
        "Content-Type": "application/json",
      }),
      body: JSON.stringify({
        email: "vkaruna7.kv@gmail.com",
      }),
    })
      .then((res) => res.json())
      .then((json) => {
        this.setState({ allData: json }, () => {
          if (value === "outForDelivery") {
            var data = this.state.allData.filter((singleData) => singleData.current_status_code === "OOD");
            this.setState({ shipmentData: data, outForDelivery: data });
          } else if (value === "nextHub") {
            var data = this.state.allData.filter((singleData) => singleData.current_status_code === "UND");
            this.setState({ shipmentData: data, transitNextHub: data });
          } else if (value === "delivered") {
            var data = this.state.allData.filter((singleData) => singleData.current_status_code === "DEL");
            this.setState({ shipmentData: data, deliveredData: data });
          } else {
            var delData = this.state.allData.filter((singleData) => singleData.current_status_code === "DEL");
            var nextHub = this.state.allData.filter((singleData) => singleData.current_status_code === "UND");
            var OODData = this.state.allData.filter((singleData) => singleData.current_status_code === "OOD");
            this.setState({
              outForDelivery: OODData,
              transitNextHub: nextHub,
              deliveredData: delData,
              shipmentData: OODData,
            });
          }
        });
      });
  };

  getSingleShipmentData = (shipmentData) => {
    console.log(shipmentData);
    this.setState({ singleShipmentData: shipmentData });
  };

  render() {
    console.log(this.state.allData);
    console.log(this.state.shipmentData);
    console.log(this.state.singleShipmentData);
    return (
      <div>
        <div
          style={{
            borderBottom: "groove",
            display: "flex",
            justifyContent: "space-between",
            height: "10vh",
            alignItems: "center",
          }}
        >
          <div>
            <span>Intugine</span>
          </div>
          <div style={{ display: "flex", width: "30%", justifyContent: "space-around" }}>
            <div>
              <span>Home</span>
            </div>
            <div>
              <span>Brands</span>
            </div>
            <div>
              <span>Transporters</span>
            </div>
            <div></div>
          </div>
        </div>
        <div>
          <div style={styles.countParentDiv}>
            <div onClick={() => this.fetchJsonData("delivered")} style={styles.DeliveryCounterDiv}>
              <span style={styles.delCounterHeaderSpan}>DEL</span>
              <span style={styles.delCounterValueSpan}>{this.state.deliveredData.length}</span>
            </div>
            <div onClick={() => this.fetchJsonData("nextHub")} style={styles.countDiv}>
              <span style={styles.countHeaderSpan}>INT</span>
              <span style={styles.counterValueSpan}>{this.state.transitNextHub.length}</span>
            </div>
            <div onClick={() => this.fetchJsonData("outForDelivery")} style={styles.countDiv}>
              <span style={styles.countHeaderSpan}>OOD</span>
              <span style={styles.counterValueSpan}>{this.state.outForDelivery.length}</span>
            </div>
            <div onClick={() => this.fetchJsonData("nextHub")} style={styles.countDiv}>
              <span style={styles.countHeaderSpan}>DEX</span>
              <span style={styles.counterValueSpan}>{this.state.transitNextHub.length}</span>
            </div>
            <div onClick={() => this.fetchJsonData("nextHub")} style={styles.countDiv}>
              <span style={styles.countHeaderSpan}>NFI</span>
              <span style={styles.counterValueSpan}>{this.state.transitNextHub.length}</span>
            </div>
          </div>
          <div style={styles.tableActionDiv}>
            <div style={styles.actionButtonDiv}>
              {this.state.singleShipmentData !== ""
                ? this.state.singleShipmentData.scan.map((singleData) => (
                    // <div style={styles.actionButtonDiv}>
                    <div style={styles.actionButtonStyle}>
                      <span style={{ fontSize: "0.7rem" }}>{singleData.status_detail}</span>
                      <span style={{ fontSize: "0.7rem" }}>{singleData.time}</span>
                      <span style={{ fontSize: "0.7rem" }}>{singleData.location}</span>
                    </div>
                    // </div>
                  ))
                : ""}
            </div>
            <div style={styles.tableDiv}>
              <TableComponent
                getSingleShipmentData={this.getSingleShipmentData}
                shipmentData={this.state.shipmentData}
              />
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default HomePageComponent;
