import React from "react";
import fetch from "node-fetch";
import axios from "axios";

const styles = {
  countDiv: {
    height: "13vh",
    backgroundColor: "blue",
    width: "7%",
    borderRadius: "10px",
    marginRight: "1%",
    display: "flex",
    flexDirection: "column",
    justifyContent: "space-around",
    alignItems: "center",
  },
  countParentDiv: {
    display: "flex",
    justifyContent: "center",
    marginTop: "3%",
  },
  countHeaderSpan: {
    fontSize: "0.79rem",
    color: "white",
    marginRight: "30%",
  },
  counterValueSpan: {
    fontSize: "1.4rem",
    color: "white",
  },
  tableStyle: {
    borderCollapse: "collapse",
    width: "100%",
  },
  tableHeaderStyle: {
    border: "1px solid #dddddd",
    textAlign: "left",
    padding: "8px",
  },
  actionButtonDiv: {
    border: "1px solid grey",
    width: "25%",
  },
  tableActionDiv: {
    display: "flex",
    justifyContent: "space-evenly",
    width: "100%",
    marginTop: "3%",
  },
  tableDiv: {
    width: "70%",
  },
  actionButtonStyle: {
    height: "40px",
    border: "1px solid grey",
    display: "flex",
    justifyContent: "space-around",
    alignItems: "center",
  },
};

class HomePageComponent extends React.Component {
  constructor(props) {
    super();
    this.state = {
      allData: [],
      deliveredData: [],
      outForDelivery: [],
      arrivedMysoreData: [],
      arrivedBangaloreData: [],
      transitNextHub: [],
    };
  }

  componentWillMount() {
    fetch("https://f0ztti2nsk.execute-api.ap-south-1.amazonaws.com/v1/consignment/fetch", {
      method: "post",
      headers: new Headers({
        Authorization: "Bearer tTU3gFVUdP",
        "Content-Type": "application/json",
      }),
      body: JSON.stringify({
        email: "vkaruna7.kv@gmail.com",
      }),
    })
      .then((res) => {
        this.setState({ allData: res.json() }, () => {
          console.log(this.state.allData);
          // if (this.state.allData.length !== 0) {
          //   var deliveredFilterData = this.state.allData.filter(
          //     (singleData) => singleData.current_status === "Delivered"
          //   );
          //   console.log(deliveredFilterData);
          // }
        });
        // res.json();
      })
      .then((json) => console.log(json));
  }
  fetchJsonData = (value) => {
    fetch("https://f0ztti2nsk.execute-api.ap-south-1.amazonaws.com/v1/consignment/fetch", {
      method: "post",
      headers: new Headers({
        Authorization: "Bearer tTU3gFVUdP",
        "Content-Type": "application/json",
      }),
      body: JSON.stringify({
        email: "vkaruna7.kv@gmail.com",
      }),
    })
      .then((res) => res.json())
      .then((json) => console.log(json));
  };

  render() {
    return (
      <div>
        <div
          style={{
            borderBottom: "groove",
            display: "flex",
            justifyContent: "space-between",
            height: "10vh",
            alignItems: "center",
          }}
        >
          <div>
            <span>Intugine</span>
          </div>
          <div style={{ display: "flex", width: "30%", justifyContent: "space-around" }}>
            <div>
              <span>Home</span>
            </div>
            <div>
              <span>Brands</span>
            </div>
            <div>
              <span>Transporters</span>
            </div>
            <div></div>
          </div>
        </div>
        <div>
          <div style={styles.countParentDiv}>
            <div style={styles.countDiv}>
              <span style={styles.countHeaderSpan}>DEL</span>
              <span style={styles.counterValueSpan}>916</span>
            </div>
            <div style={styles.countDiv}>
              <span style={styles.countHeaderSpan}>INT</span>
              <span style={styles.counterValueSpan}>916</span>
            </div>
            <div style={styles.countDiv}>
              <span style={styles.countHeaderSpan}>OOD</span>
              <span style={styles.counterValueSpan}>916</span>
            </div>
            <div style={styles.countDiv}>
              <span style={styles.countHeaderSpan}>DEX</span>
              <span style={styles.counterValueSpan}>916</span>
            </div>
            <div style={styles.countDiv}>
              <span style={styles.countHeaderSpan}>NFI</span>
              <span style={styles.counterValueSpan}>916</span>
            </div>
          </div>
          <div style={styles.tableActionDiv}>
            <div style={styles.actionButtonDiv}>
              <div style={styles.actionButtonStyle} onClick={() => this.fetchJsonData("delivered")}>
                <span>Delivered</span>
                <span>04-07-2019</span>
                <span>14:00</span>
              </div>
              <div style={styles.actionButtonStyle} onClick={() => this.fetchJsonData("outForDelivery")}>
                <span>Out Of Delivery</span>
                <span>04-07-2019</span>
                <span>14:00</span>
              </div>
              <div style={styles.actionButtonStyle} onClick={() => this.fetchJsonData("arrivedForMySure")}>
                <span>Arrived at Mysore</span>
                <span>04-07-2019</span>
                <span>14:00</span>
              </div>
              <div style={styles.actionButtonStyle} onClick={() => this.fetchJsonData("arrivedAtBangalore")}>
                <span>Arrived at Bangalore</span>
                <span>04-07-2019</span>
                <span>14:00</span>
              </div>
              <div style={styles.actionButtonStyle} onClick={() => this.fetchJsonData("nextHub")}>
                <span>Transit to Next Hub</span>
                <span>04-07-2019</span>
                <span>14:00</span>
              </div>
            </div>
            <div style={styles.tableDiv}>
              <table style={styles.tableStyle}>
                <tr>
                  <th style={styles.tableHeaderStyle}>Company</th>
                  <th style={styles.tableHeaderStyle}>Contact</th>
                  <th style={styles.tableHeaderStyle}>Country</th>
                </tr>
                <tr>
                  <td style={styles.tableHeaderStyle}>Alfreds Futterkiste</td>
                  <td style={styles.tableHeaderStyle}>Maria Anders</td>
                  <td style={styles.tableHeaderStyle}>Germany</td>
                </tr>
                <tr>
                  <td style={styles.tableHeaderStyle}>Centro comercial Moctezuma</td>
                  <td style={styles.tableHeaderStyle}>Francisco Chang</td>
                  <td style={styles.tableHeaderStyle}>Mexico</td>
                </tr>
              </table>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default HomePageComponent;
